import Express from "express";

import webpack from "webpack";

import config from "../webpack.config.js";

import mongoose from "mongoose";

import exphbs from "express-handlebars";

import passport from "passport";

import {
	createMemoryHistory
} from "history";

import {
	renderToString
} from "react-dom/server"

import {
	RouterContext,
	match,
	Route,
	IndexRoute
} from "react-router";

import React from "react"
import {Provider} from "react-redux"

import bodyParser from "body-parser";

import routesFunction from "../client/routes.js";

import configureStore from "../client/store/configureStore"

console.log(config);
const compiler = webpack(config);

const app = Express();

const PORT = process.env.PORT || 3000;

console.log(process.env.MONGO_URI);
mongoose.connect(process.env.MONGO_URI);

app.use(bodyParser.json());

app.use(require('webpack-dev-middleware')(compiler, {
  noInfo: true,
  publicPath: config.output.publicPath
}));

app.use(require('webpack-hot-middleware')(compiler));

app.engine('handlebars', exphbs({defaultLayout: 'main'}));
app.set('view engine', 'handlebars');

app.use(require('express-session')({ secret: 'keyboard cat', resave: true, saveUninitialized: true }));

app.use(passport.initialize());

app.use(passport.session());

app.use("/api", require("./api/api"));
app.get("/*", function(req, res){

  const history = createMemoryHistory();
  console.log(req.originalUrl);
  const location = history.createLocation({
    pathname: req.originalUrl
  })

  var routes = routesFunction();

  match({routes, location}, function(err, redirectLocation, renderProps){
    if(err) {
      console.error(err);
      return res.status(500).end('Internal server error');
    }

    if(!renderProps) {
      return res.status(404).end('Not found');
    }
    const store = configureStore();
    const InitialView = (
      <Provider className="root" store={store}>
          <RouterContext {...renderProps} />
      </Provider>
    );

    const finalState = store.getState();
    const html = renderToString(InitialView)
    res.status(200).end(renderFullPage(html, finalState));
  })
});

function renderFullPage(html, initialState) {
  return `
    <!doctype html>
    <html lang="en">
      <head>
        <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.3.5/css/bootstrap.min.css" />
        <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/font-awesome/4.4.0/css/font-awesome.min.css" />
        <link rel="icon" href="./favicon.ico" type="image/x-icon" />
        <meta charset="utf-8">
        <meta name="viewport" content="width=device-width, initial-scale=1, maximum-scale=1, user-scalable=0" />
        <title>React Redux Socket.io Chat</title>
      </head>
      <body>
        <container id="react">${html}</container>
        <script>
          window.__INITIAL_STATE__ = ${JSON.stringify(initialState)}
        </script>
        <script src="/bundle.js"></script>
      </body>
    </html>
  `
}
app.listen(PORT, function(){
  console.log("Server is listening on port "+PORT);
});

