import React,{Component} from 'react';
import {browserHistory} from "react-router";
import {connect} from "react-redux";
import Nav from "./Nav";
import {getAllBooks} from "./../actions/action";
import Book from "./Book"

function mapStateToProps(state){
  return state;
}

@connect(mapStateToProps,{getAllBooks})
export default class Books extends Component{
  constructor(props){
    super(props);
  }
  componentDidMount(){
    this.props.getAllBooks();
  }
  render(){
    let username=null;
    if(this.props.user){
      username=this.props.user.username;
    }
    return(
      <div>
        <Nav user={this.props.user} />
        <h2>Books</h2>
        <div>
          {this.props.books && this.props.books.length==0? <strong>There are no books. Add Some.</strong> : this.props.books.map((book,i)=> <Book key={i} username={username} book={book} />)}
        </div>
      </div>
    )
  }
}

