import React, {Component} from 'react';
import {connect} from "react-redux";
import { addBooks, getAllMyBooks } from "./../actions/action";
import {browserHistory} from "react-router";
import Logout from "./Logout.js"
import Nav from "./Nav";
import Mybook from "./mybook.js";

function mapStateToProps(state){
  return state;
}

@connect(mapStateToProps, {addBooks, getAllMyBooks})
export default class Mybooks extends Component {
  constructor(props){
    super(props);
    this.state={
      addbook:""
    }
  }
  
  handleChange(e){
    this.setState({
      addbook: e.target.value
    });
  }
  handleSubmit(e){
    e.preventDefault();
    this.props.addBooks(this.state.addbook);
    this.setState({
      addbook:""
    });
  }
  componentDidMount(){
    if(!this.props.user){
      console.log("user is not logged in. redirecting to login");
      browserHistory.push("/login");
    }
    else{
      this.props.getAllMyBooks();
    }
  }
  render(){
    return (
      <div>
        <Nav user={this.props.user} />
        <div>
          <form onSubmit={this.handleSubmit.bind(this)}>
            <input type="text" onChange={this.handleChange.bind(this)} value={this.state.addbook} />
            <button type="submit">Submit</button>
          </form>
        </div>
        <div>
          {this.props.user && this.props.mybooks.length!=0 && this.props.requestedBooks.length!=0 &&
          this.props.requestedBooks.map((request,i)=><div><h4>{request.book.title}</h4><img src={request.book.cover} /><h3>Requested by {request.requestor}</h3></div>)}
        </div>
        <div>
          {this.props.user && this.props.mybooks.length==0 ? <p>You have no books. Add some books.</p> : this.props.mybooks.map((book,i)=><Mybook key={i} book={book} />)}
        </div>
        <Logout />
      </div>  
    );
  }
};

