import React,{Component} from "react";
import {deleteBook} from "./../actions/action";
import {connect} from "react-redux";

@connect(null, {deleteBook})
export default class Mybook extends Component{
  constructor(props){
    super(props);
    this.state={}
  }

  delete(){
    const bookID = this.props.book._id; 
    this.props.deleteBook(bookID);  
  }

  render(){
    return(
      <div>
        <h3>{this.props.book.title}</h3><img src={this.props.book.cover} />
        <button onClick={this.delete.bind(this)}>Delete</button>
      </div>
    );
  }
}

