import React,{Component} from "react";
import {Link} from "react-router";
import {browserHistory} from "react-router";
import Nav from "./Nav";

export default class Signup extends Component{
  constructor(props){
    super(props);
    this.state={
      username:"",
      password:"",
      err:null
    }
  }

  handleChange(e){
    if(e.target.name=="username"){
      this.setState({
        "username": e.target.value
      });
    }
    if(e.target.name=="password"){
      this.setState({
        "password": e.target.value
      });
    }
  }

  handleSubmit(e){
    e.preventDefault();
    const {username,password} = this.state;
    var _this = this;
    if(username==""){
      this.setState({
        err:"Please enter username"
      });
    }
    else if(password==""){
      this.setState({
        err:"Please enter password"
      });
    }
    else{
      fetch("/api/signup",{
        headers: {
    				'Accept': 'application/json',
    				'Content-Type': 'application/json'
    			},
    			method: 'POST',
    			body: JSON.stringify({
    				"username": _this.state.username,
    				"password": _this.state.password
    		})
      }).then(response=>response.json()).then(json=>{
        if(json.success){
          browserHistory.push("/login");
        } else{
          _this.setState({
            err:json.msg
          });
        }
      });
    }
  }

  render(){
    return(
      <div>
        <Nav user={this.props.user} />
        <h1>Signup</h1>
        {this.state.err && <span style={{color:"red"}}>{this.state.err}</span>}
        <form onSubmit={this.handleSubmit.bind(this)}>
          <input type="text" name="username" onChange={this.handleChange.bind(this)} value={this.state.username} /><br/>
          <input type="text" name="password" onChange={this.handleChange.bind(this)} value={this.state.password} /><br/>
          <button type="submit">Signup</button>&nbsp;<Link to="/login"><button>Login</button></Link>
        </form>
      </div>
    );
  }
}

