import {createStore,applyMiddleware,compose} from "redux";
import thunkMiddleware from "redux-thunk";
import reduxImmutableStateInvariant from 'redux-immutable-state-invariant';
import createLogger from "redux-logger";

const Initial_State={
  "test":"test",
  "data":"",
  "mybooks":[],
  "books": [],
  "wrongAuthentication": false,
  "user": null,
  "requestedBooks":[]
};

const reducer = function(state=Initial_State, action){
  switch(action.type){
    case "authentication":
      return Object.assign({}, state, {
        wrongAuthentication:action.wrongAuthentication,
        user: action.user
      });
    case "logout":
      return Object.assign({}, state, {
        mybooks:[],
        user:null,
        wrongAuthentication:false
      })

    case "addbook":
      return Object.assign({}, state, {
        mybooks:[...state.mybooks, action.book]
      })

    case "booksOfUser":
      return Object.assign({}, state,{
        mybooks:action.books
      })

    case "removeBook":
      return Object.assign({}, state, {
        mybooks: state.mybooks.filter(book=>{
          if(book._id==action.id)
            return false;
          return true;
        })
      })
    case "setAllBooks":
      return Object.assign({}, state, {
        books: action.books
      })

    case "requestedBook":
      return Object.assign({}, state, {
        requestedBooks:[...state.requestedBooks, action.request]
      });
    default:
      return state;
  }
}
export default function configureStore(initialState) {
  const middewares = [
    // Add other middleware on this line...
    createLogger(),
    // Redux middleware that spits an error on you when you try to mutate your state either inside a dispatch or between dispatches.
    reduxImmutableStateInvariant(),

    // thunk middleware can also accept an extra argument to be passed to each thunk action
    // https://github.com/gaearon/redux-thunk#injecting-a-custom-argument
    thunkMiddleware,
  ];
  const store = createStore(reducer, initialState, compose(
    applyMiddleware(...middewares)
    )
  );
  return store;
}

