'use strict';

var google = require("googleapis");
var books = google.books('v1');

var User = require("./../models/User");
var Book = require("./../models/Book");
var passport = require("passport");
var LocalStrategy = require("passport-local").Strategy;

var API_KEY = process.env.Google_Key;

var router = require("express").Router();

router.post("/addbook", function (req, res) {
  console.log(req.body);
  var bookname = req.body.addbook;
  books.volumes.list({
    auth: API_KEY,
    q: bookname
  }, function (err, data) {
    var newbook = new Book({
      title: data.items[0].volumeInfo.title,
      owner: req.body.user,
      cover: data.items[0].volumeInfo.imageLinks == 'undefined' ? '' : data.items[0].volumeInfo.imageLinks.thumbnail,
      requested: false
    });
    newbook.save(function (err, data) {
      if (err) throw err;
      res.send(data);
    });
  });
});

router.post("/getbooksofuser", function (req, res) {
  var user = req.body.user;
  Book.find({
    owner: user
  }).exec(function (err, data) {
    if (err) throw err;
    res.send({
      success: true,
      books: data
    });
  });
});

//   #################
//   Dummy apis
//   #################
router.get("/getallbooks", function (req, res) {
  Book.find({}).exec(function (err, data) {
    if (err) throw err;
    res.send(data);
  });
});

router.get("/getallusers", function (req, res) {
  User.find({}).exec(function (err, data) {
    if (err) throw err;
    res.send(data);
  });
});

//   #################
//   Dummy apis
//   #################
router.post("/signup", function (req, res) {
  var user = req.body;
  User.findOne({
    username: user.username
  }).exec(function (err, data) {
    if (err) throw err;
    if (data) {
      res.send({
        success: false,
        msg: "username is taken"
      });
    } else {
      User.create(user, function (err, data) {
        if (err) throw err;
        res.send({
          success: true
        });
      });
    }
  });
});

passport.serializeUser(function (user, done) {
  done(null, user.username);
});

passport.deserializeUser(function (username, done) {
  User.findOne({
    username: username
  }, function (err, user) {
    done(err, user);
  });
});

passport.use(new LocalStrategy(function (username, password, done) {
  User.findOne({
    username: username
  }, function (err, user) {
    if (err) throw err;
    if (!user) {
      return done(null, false);
    } else {
      if (password == user.password) {
        return done(null, user);
      } else {
        return done(null, false);
      }
    }
  });
}));

router.post('/login', function (req, res, next) {
  passport.authenticate('local', function (err, user, info) {
    if (err) {
      return next(err);
    }
    if (!user) {
      return res.send({
        "success": false,
        "user": null
      });
    }
    req.logIn(user, function (err) {
      if (err) {
        return next(err);
      }
      return res.send({
        "success": true,
        "user": user
      });
    });
  })(req, res, next);
});

router.post("/removebook", function (req, res) {
  var ID = req.body.id;
  console.log(ID);
  Book.findOne({
    _id: ID
  }, function (err, data) {
    if (err) throw err;
    data.remove();
    res.send({
      success: true
    });
  });
});
module.exports = router;
