import React, {Component} from "react";
import {connect} from "react-redux";
import {requestBook} from "./../actions/action";

@connect(null,{requestBook})
export default class Book extends Component {
  constructor(props){
    super(props);
  }
  request(){
    this.props.requestBook(this.props.username, this.props.book);
  }
  render(){
    return(
      <div>
        <h3>{this.props.book.title}</h3><img src={this.props.book.cover} />
        {this.props.book.owner==this.props.username ? <strong>This book is yours</strong> : <button onClick={this.request.bind(this)}>Request</button>}
      </div>
    );
  }
}
